-- OUTRO
outro_01_narrator_as_the_evil = [[Assim que o mal desapareceu, o velho rei, ou o pouco que restava da sua alma humana, foi libertado.]]
outro_02_narrator_the_artifacts_slowly = [[Os artefactos restauraram lentamente o equilíbrio no mundo. A vida e as suas alegrias regressaram ao reino.]]
outro_03_narrator_in_the_spring = [[Na Primavera, foi eleito um novo rei.]]
outro_04_narrator_the_new_king = [[O novo rei ofereceu recompensas aos nossos heróis. O cavaleiro recusou tornar-se no capitão do Exército do Rei. Em vez disso, correspondeu finalmente às expectativas do seu pai e admitiu a sua outra paixão: comida e cerveja. Tornou-se o fornecedor real de cerveja do rei.]]
outro_05_narrator_the_thief_was = [[À ladra, ofereceu riquezas que iam além dos seus sonhos mais extravagantes, mas esta sentia o desejo de regressar às ruínas na floresta. O rei concedeu-lhe esse desejo e presenteou-a com um reinado sobre a floresta, onde a vida regressou lentamente, voltando ao seu antigo esplendor.]]
outro_06_narrator_as_for_the = [[Quanto ao feiticeiro... Percebeu finalmente que o caminho para o sucesso nada tinha nada a ver com o feitiço da bola de fogo. Não sabia o que era, mas salvar o reino tê-lo-á certamente ajudado.]]
outro_07_narrator_a_few_years = [[Após estar casado alguns anos com uma senhora chamada Margaret, esta deu à luz trigémeos. Um momento encantador, segundo os dizeres de muitas pessoas.]]
outro_08_narrator_we_also_do = [[Também não sabemos se o pai terá sentido orgulho, vergonha, ou ambas as coisas, quando todas as crianças, antes de completarem um ano de idade, já dominavam o feitiço da bola de fogo.]]
outro_09_narrator_but_despite_everything = [[Mas, apesar de tudo... Viveram todos felizes para sempre.]]

